contracts/RockPaperScissors.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract RockPaperScissors {
    // 게임 결과를 저장하는 이벤트
    event GamePlayed(address player, uint8 playerChoice, uint8 contractChoice, uint8 result, uint256 payout);

    // 게임 비용
    uint256 public constant GAME_COST = 0.01 ether;
    
    // 게임 결과 상수
    uint8 public constant LOSS = 0;
    uint8 public constant DRAW = 1;
    uint8 public constant WIN = 2;

    // 가위바위보 선택 상수 (블록넘버 % 3과 일치하도록)
    uint8 public constant SCISSORS = 2;  // 가위
    uint8 public constant ROCK = 1;      // 바위
    uint8 public constant PAPER = 0;     // 보

    // 컨트랙트 소유자
    address public owner;

    constructor() {
        owner = msg.sender;
    }

    // 게임 플레이 함수
    function play(uint8 _playerChoice) public payable {
        // 정확한 금액이 전송되었는지 확인
        require(msg.value == GAME_COST, "Please send exactly 0.01 ETH");
        // 올바른 선택인지 확인
        require(_playerChoice <= 2, "Invalid choice");

        // 현재 블록 넘버로 컨트랙트의 선택 결정
        uint8 contractChoice = uint8(block.number % 3);
        
        // 게임 결과 계산
        uint8 result = determineWinner(_playerChoice, contractChoice);
        
        // 상금 지급
        uint256 payout = 0;
        if (result == WIN) {
            payout = GAME_COST * 2;
            (bool success, ) = payable(msg.sender).call{value: payout}("");
            require(success, "Failed to send payout");
        } else if (result == DRAW) {
            payout = GAME_COST;
            (bool success, ) = payable(msg.sender).call{value: payout}("");
            require(success, "Failed to send payout");
        }

        // 이벤트 발생
        emit GamePlayed(msg.sender, _playerChoice, contractChoice, result, payout);
    }

    // 승자 결정 함수
    function determineWinner(uint8 _playerChoice, uint8 _contractChoice) private pure returns (uint8) {
        if (_playerChoice == _contractChoice) {
            return DRAW;
        }
        
        if (
            (_playerChoice == ROCK && _contractChoice == SCISSORS) ||
            (_playerChoice == PAPER && _contractChoice == ROCK) ||
            (_playerChoice == SCISSORS && _contractChoice == PAPER)
        ) {
            return WIN;
        }
        
        return LOSS;
    }

    // 컨트랙트 잔액 확인
    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }

    // 컨트랙트 소유자만 잔액 인출 가능
    function withdraw() public {
        require(msg.sender == owner, "Only owner can withdraw");
        (bool success, ) = payable(owner).call{value: address(this).balance}("");
        require(success, "Withdrawal failed");
    }

    // 컨트랙트가 ETH를 받을 수 있도록 하는 함수
    receive() external payable {}
}
